import { useState } from 'react'
import { Container } from "./styles"

interface TimeButtonProps {
  status: number;
}

export function TimeButton({}:TimeButtonProps) {
  const [status, setStatus] = useState(0)

  function handleButton() {
    setStatus(status + 1)
  }

  function getTime() {
    const timestamp = Date.now()
    new Intl.DateTimeFormat('en-US',
    {year: 'numeric', month: '2-digit',day: '2-digit', hour: '2-digit', minute: '2-digit', second: '2-digit'}).format(timestamp)
  }
  console.log(getTime)

  return(
    <Container>
      {(status === 0) &&
      <button onClick={() => {handleButton(), getTime}}>Entry Time</button>}

      {(status === 1) &&
      <button onClick={handleButton}>Begin Launch Break</button>}

      {(status === 2) &&
      <button onClick={handleButton}>End Launch Break</button>}

      {(status === 3) &&
      <button onClick={handleButton}>Exit Time</button>}

      {(status >= 4) &&
      <button onClick={handleButton} disabled> See you tomorrow ðŸ˜‰ </button>}
    </Container>
  );
}
