import { useEffect, useState } from "react";
import { api } from "../../services/api";
import { Container } from "./styles";

interface TimeTable {
  id: number;
  day: string;
  entry: string;
  launchbreak: number;
  exit: string;
  worked: number;
}

export function TimeTable() {
  const [timeTables, setTimeTables] = useState<TimeTable[]>([])

  useEffect(() => {
    api.get('timetable')
    .then(response => setTimeTables(response.data.timetables))
  }, []);

  return(
    <Container>
      <table>
        <thead>
          <tr>
            <th>Day</th>
            <th>Entry time</th>
            <th>Launch break</th>
            <th>Exit time</th>
            <th>Worked time</th>
          </tr>
        </thead>

        <tbody>
          {timeTables.map(timeTable => (
            <tr key={timeTable.id}>
              <td>{new Intl.DateTimeFormat().format(
                new Date(timeTable.day)
                )}
              </td>
              <td className="entry">{new Intl.DateTimeFormat('US-EN', {
                timeStyle: 'medium'
              }).format(
                new Date(timeTable.exit)
                )}
              </td>
              <td>{timeTable.launchbreak}</td>
              <td>{new Intl.DateTimeFormat('US-EN', {
                timeStyle: 'medium'
              }).format(
                new Date(timeTable.exit)
                )}
              </td>
              <td>{timeTable.worked}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </Container>
  );
}
