import styled from 'styled-components'

export const Container = styled.div`
  display: flex;
  justify-content: space-around;  
  margin-top: -10rem;

  div {
    background: var(--shape);
    padding: 1.5rem 2rem;
    border-radius: 0.25rem;

  }

  svg {
    height: 1rem;
    width: 1rem;
  }
`
